def gen_icon(data, icon_size):
    png_bytes = cairosvg.svg2png(bytestring=data,
                                 output_width=icon_size[0],
                                 output_height=icon_size[1],
                                 scale=1)
    return Image.open(io.BytesIO(png_bytes)).convert("RGBA")

